graphql.query:
query Anichartuser {
  AniChartUser {
    user {
      id
      name
      about
      avatar {
        large
        medium
      }
      bannerImage
      isFollowing
      isFollower
      isBlocked
      bans
      options {
        titleLanguage
        displayAdultContent
        airingNotifications
        profileColor
        notificationOptions {
          type
          enabled
        }
        timezone
        activityMergeTime
      }
      mediaListOptions {
        scoreFormat
        rowOrder
        animeList {
          sectionOrder
          splitCompletedSectionByFormat
          customLists
          advancedScoring
          advancedScoringEnabled
        }
        mangaList {
          sectionOrder
          splitCompletedSectionByFormat
          customLists
          advancedScoring
          advancedScoringEnabled
        }
      }
      statistics {
        anime {
          count
          meanScore
          standardDeviation
          minutesWatched
          episodesWatched
          chaptersRead
          volumesRead
        }
        manga {
          count
          meanScore
          standardDeviation
          minutesWatched
          episodesWatched
          chaptersRead
          volumesRead
        }
      }
      unreadNotificationCount
      siteUrl
      donatorTier
      donatorBadge
      moderatorRoles
      updatedAt
    }
    settings
    highlights
  }
}

query Sitestatistics {
  SiteStatistics {
    users(sort: $usersSort, page: $usersPage, perPage: $usersPerPage) {
      edges {
        node {
          date
          count
          change
        }
      }
      nodes {
        date
        count
        change
      }
      pageInfo {
        total
        perPage
        currentPage
        lastPage
        hasNextPage
